// RoleInput.js
// RoleInput.js
import React from 'react';
import styled from 'styled-components'; // Import styled-components
import { Input } from './Components'; // Import the Input component style from Components

const RoleContainer = styled.div`
  /* Apply the same styling as the Input component */
  background-color: #eee;
  border: none;
  padding: 12px 15px;
  margin: 8px 0;
  width: 100%;
`;

const Role = () => {
    const roles = ['Admin', 'Manager', 'Employee']; // List of predefined roles

    return (
        <RoleContainer>
            <select>
                {roles.map((role, index) => (
                    <option key={index} value={role}>{role}</option>
                ))}
            </select>
        </RoleContainer>
    );
}

export default Role;
