import React, { useState } from "react";

import {
    GhostButton,
    LeftOverlayPanel, Overlay,
    OverlayContainer,
    Paragraph,
    RightOverlayPanel,
    SignInContainer,
    SignUpContainer, Title
} from "./Styles";
import SignUpPage from "./Components/SignUpPage";
import SignInPage from "./Components/SignInPage";


function App() {
    const [signIn, setSignIn] = useState(true);

    const handleSignUpClick = () => {
        setSignIn(false); // Set signIn to false to display the sign-up form
    };

    const handleSignInClick = () => {
        setSignIn(true); // Set signIn to true to display the sign-in form
    };

    return (
        <>
            <SignUpContainer signinIn={signIn}>
                <SignUpPage toggle={handleSignInClick} />
            </SignUpContainer>
            <SignInContainer signinIn={signIn}>
                <SignInPage toggle={handleSignUpClick} />
            </SignInContainer>
            <OverlayContainer signinIn={signIn}>
                <Overlay signinIn={signIn}>
                    <LeftOverlayPanel signinIn={signIn}>
                        <Title>Welcome Back!</Title>
                        <Paragraph>
                            To keep connected with us please login with your personal info
                        </Paragraph>
                        <GhostButton onClick={handleSignInClick}>
                            Sign In
                        </GhostButton>
                    </LeftOverlayPanel>
                    <RightOverlayPanel signinIn={signIn}>
                        <Title>Hello, Friend!</Title>
                        <Paragraph>
                            Enter Your personal details and start journey with us
                        </Paragraph>
                        <GhostButton onClick={handleSignUpClick}>
                            Sign Up
                        </GhostButton>
                    </RightOverlayPanel>
                </Overlay>
            </OverlayContainer>
        </>
    );
}

export default App;
