import React, { useState } from "react";
import {
    Button,
    Container, LeftOverlayPanel,
    Overlay,
    OverlayContainer,
    Paragraph,
    RightOverlayPanel,
    SignInContainer,
    SignUpContainer
} from './Styles';
import SignInPage from './Components/SignInPage';
import SignUpPage from './Components/SignUpPage';

function App() {
    const [signIn, setSignIn] = useState(true);

    const toggle = () => {
        setSignIn(!signIn); // Toggle the value of signIn
    };

    return (
        <Container>
            <SignUpContainer signIn={signIn}>
                <SignUpPage toggle={toggle} />
            </SignUpContainer>
            <SignInContainer signIn={signIn}>
                <SignInPage toggle={toggle} />
            </SignInContainer>
            <OverlayContainer signIn={signIn}>
                <Overlay signIn={signIn}>
                    <LeftOverlayPanel signIn={signIn}>
                        <Paragraph>Welcome Back!</Paragraph>
                        <Paragraph>To keep connected with us please login with your personal info</Paragraph>
                        <Button onClick={toggle}>Sign In</Button>
                    </LeftOverlayPanel>
                    <RightOverlayPanel signIn={signIn}>
                        <Paragraph>Hello, Friend!</Paragraph>
                        <Paragraph>Enter your personal details and start your journey with us</Paragraph>
                        <Button onClick={toggle}>Sign Up</Button>
                    </RightOverlayPanel>
                </Overlay>
            </OverlayContainer>
        </Container>
    );
}

export default App;
